import React from 'react'

const Tours = ({tours}) => {

    

    return (
        <>
        <section className="whole">
        <h2>Our Tours</h2>  
          <div className="underline"></div>
         <div>
             {
                tours.map((currElem) => {

                    return(

                        <main className="singleTour" key={currElem.id}>
                            <div className="ImageContainer">
                                <img src={currElem.image} className="pic"/>
                            </div>

                            <div className="TextContainer">

                                <div className="heading">
                                    <div className="nameOfTour">
                                        {currElem.name}
                                    </div>

                                    <div className="PriceOfTour">
                                       ${currElem.price}
                                    </div>
                                </div>

                                <div className="Description">

                                        {currElem.info.substring(0,200)}.....;

                                </div>

                                <div class="readmore" onClick={
                                    () => {
                                        const singleDiv=document.querySelectorAll('.Description');
                                        singleDiv.forEach((item ) => {

                                            item.addEventListener('click',()=>{

                                                singleDiv.textContent=currElem.info;

                                            });

                                        });

                                }}>
                                    Read More
                                </div>

                                <div class="showless">
                                    Show Less
                                </div>
                            </div>

                            <button>
                                Not Interested
                            </button>
                        </main>
                    )

                }) 
             }
         </div>
        </section>
        
        </>
    )
}

export default Tours
